const header=document.querySelector("[data-header]"),headerInner=document.querySelector("[data-header-inner]");window.addEventListener("scroll",function(){window.scrollY>80?(header.classList.add("header--scroll"),headerInner.classList.add("header__inner--scroll")):(header.classList.remove("header--scroll"),headerInner.classList.remove("header__inner--scroll"))});const range=document.querySelector("[data-range]"),rangeValue=document.querySelector("[data-range-value]");range.addEventListener("input",function(){rangeValue.innerText=this.value});const burger=document.querySelector("[data-burger]"),nav=document.querySelector("[data-nav]");burger.addEventListener("click",e=>{e.preventDefault(),burger.classList.toggle("burger__active"),nav.classList.toggle("nav__active"),burger.classList.contains("burger__active")?document.body.style.overflow="hidden":document.body.style.overflow=""});